    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_home_screen);

        ActivityCompat.requestPermissions(this, new String[]{android.Manifest.permission.ACCESS_FINE_LOCATION}, REQUEST_LOCATION);

        mFusedLocationProviderClient = LocationServices.getFusedLocationProviderClient(this);

        if (ActivityCompat.checkSelfPermission(HomeScreen.this, Manifest.permission.ACCESS_FINE_LOCATION)
                != PackageManager.PERMISSION_GRANTED && ActivityCompat.checkSelfPermission
                (HomeScreen.this, Manifest.permission.ACCESS_COARSE_LOCATION) != PackageManager.PERMISSION_GRANTED) {
            ActivityCompat.requestPermissions(HomeScreen.this, new String[]{Manifest.permission.ACCESS_FINE_LOCATION}, REQUEST_LOCATION);
        }

        final EditText editText = (EditText) findViewById(R.id.editText);
        editText.setOnFocusChangeListener(this);

        editText.setOnFocusChangeListener(new View.OnFocusChangeListener() {
            @Override
            public void onFocusChange(View v, boolean hasFocus) {
                if (!hasFocus) {
                    if (editText.getText().toString().length() == 5) {
                        zip_code = editText.getText().toString();
                        Intent myIntent = new Intent(v.getContext(), CongressionalView.class);
                        myIntent.putExtra("zip_code", zip_code);
                        startActivityForResult(myIntent, 0);
                    }
                    hideKeyboard(v);
                }
            }
        });


        try {
            locationManager = (LocationManager) getSystemService(Context.LOCATION_SERVICE);
            locationManager.requestLocationUpdates(LocationManager.GPS_PROVIDER,
                    (long) 0,
                    (float) 1000, locationListenerGPS);
        } catch (java.lang.SecurityException e) {
            System.out.println("PERMISSION NOT GRANTED");
        }

        FloatingActionButton current_loc = (FloatingActionButton)findViewById(R.id.current_loc);
        current_loc.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                if (!locationManager.isProviderEnabled(LocationManager.GPS_PROVIDER)) {
                    buildAlertMessageNoGps();
                } else if (locationManager.isProviderEnabled(LocationManager.GPS_PROVIDER)) {
                    zip_code = getLocation();
                    if (zip_code != null) {
                        Intent myIntent = new Intent(v.getContext(), CongressionalView.class);
                        myIntent.putExtra("zip_code", zip_code);
                        startActivityForResult(myIntent, 0);
                    }
                }
            }
        });

        FloatingActionButton random_loc = (FloatingActionButton) findViewById(R.id.random_loc);
        random_loc.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Random rand = new Random();
                int n = rand.nextInt(all_zips.length);
                zip_code = all_zips[n];
                if (zip_code != null) {
                    Intent myIntent = new Intent(view.getContext(), CongressionalView.class);
                    myIntent.putExtra("zip_code", zip_code);
                    startActivityForResult(myIntent, 0);
                }
            }
        });
    }
